console.log(anotherCar.getDetails()); // Honda Civic

function ProductFactory() {
  this.createProduct = function(type, options) {
    let product;

    switch (type) {
      case 'laptop':
        product = { name: options.name, price: options.price, cpu: options.cpu };
        break;
      case 'smartphone':
        product = { name: options.name, price: options.price, os: options.os };
        break;
      default:
        throw new Error('Unknown product type');
    }

    product.getType = function() { return type; };
    product.getFormattedPrice = function() { return `$${this.price.toFixed(2)}`; };

    return product;
  };
}

const factory = new ProductFactory();
const laptop = factory.createProduct('laptop', { name: 'XPS 15', price: 1500, cpu: 'i7' });
