const Singleton = (function() {
  let instance;

  function createInstance() {
    // This function creates the actual object instance
    return {
      data: [],
      addItem: function(item) {
        this.data.push(item);
        console.log(`Item added: ${item}. Current data: ${this.data}`);
      },
      getData: function() {
        return this.data;
      }
    };
  }

  return {
    // The public method to get the single instance
    getInstance: function() {
      if (!instance) {
        instance = createInstance(); // Create instance only if it doesn't exist
      }
      return instance;
    }
  };
})();

const instance1 = Singleton.getInstance();
instance1.addItem('Apple');

const instance2 = Singleton.getInstance();
instance2.addItem('Banana');

console.log(instance1 === instance2); // true - Both variables reference the exact same object
console.log(instance1.getData()); // ['Apple', 'Banana'] - Data is shared
console.log(instance2.getData()); // ['Apple', 'Banana']

// Old Calculator with a single 'operate' method handling all operations
class OldCalculator {
  operate(term1, term2, operation) {
    switch(operation) {
      case 'add': return term1 + term2;
      case 'sub': return term1 - term2;
      default: return NaN;
    }
  }
}

// New Calculator with separate methods for each operation
class NewCalculator {
  add(term1, term2) {
    return term1 + term2;
  }

  sub(term1, term2) {
    return term1 - term2;
  }

  multiply(term1, term2) {
    return term1 * term2;
  }
}

// Adapter: Makes NewCalculator compatible with OldCalculator's 'operate' interface
class CalcAdapter {
  constructor() {
    this.calc = new NewCalculator(); // The Adaptee
  }

  // Implements the target interface (OldCalculator's operate method signature)
  operate(term1, term2, operation) {
    switch(operation) {
      case 'add': return this.calc.add(term1, term2);
      case 'sub': return this.calc.sub(term1, term2);
      // Can also add new operations from NewCalculator if needed
      case 'multiply': return this.calc.multiply(term1, term2);
      default: return NaN;
    }
  }
}

// Usage with OldCalculator (Client expects this interface)
const oldCalc = new OldCalculator();
console.log('Old Calculator (add):', oldCalc.operate(10, 5, 'add')); // 15
console.log('Old Calculator (sub):', oldCalc.operate(10, 5, 'sub')); // 5

// Usage with Adapter (Client still uses the 'operate' interface, but NewCalculator logic is behind it)
const newCalcAdapter = new CalcAdapter();
console.log('New Calculator (via Adapter add):', newCalcAdapter.operate(10, 5, 'add')); // 15
console.log('New Calculator (via Adapter sub):', newCalcAdapter.operate(10, 5, 'sub')); // 5
console.log('New Calculator (via Adapter multiply):', newCalcAdapter.operate(10, 5, 'multiply')); // 50

/**
 * Component: Defines the interface for objects that can be decorated.
 * In this case, Coffee is the base component.
 */
function Coffee() {
  this.cost = function() {
    return 5;
  };
  this.getDescription = function() {
    return 'Basic Coffee';
  };
}

/**
 * Concrete Decorator: Adds Milk functionality to a coffee object.
 * It wraps an existing coffee object and modifies its cost and description.
 */
function Milk(coffee) {
  const originalCost = coffee.cost();
  const originalDescription = coffee.getDescription();

  coffee.cost = function() {
    return originalCost + 1; // Add cost for milk
  };
  coffee.getDescription = function() {
    return originalDescription + ', Milk'; // Add description for milk
  };
}

/**
 * Concrete Decorator: Adds Whip functionality to a coffee object.
 */
function Whip(coffee) {
  const originalCost = coffee.cost();
  const originalDescription = coffee.getDescription();

  coffee.cost = function() {
    return originalCost + 0.5; // Add cost for whip
  };
  coffee.getDescription = function() {
    return originalDescription + ', Whip'; // Add description for whip
  };
}

// Usage:
let myCoffee = new Coffee();
console.log(`Cost: ${myCoffee.cost()}, Description: ${myCoffee.getDescription()}`); // Cost: 5, Description: Basic Coffee

Milk(myCoffee); // Decorate with Milk
console.log(`Cost: ${myCoffee.cost()}, Description: ${myCoffee.getDescription()}`); // Cost: 6, Description: Basic Coffee, Milk

Whip(myCoffee); // Decorate with Whip (after Milk)
console.log(`Cost: ${myCoffee.cost()}, Description: ${myCoffee.getDescription()}`); // Cost: 6.5, Description: Basic Coffee, Milk, Whip

// Another example: Coffee with only Whip
