type Theme = 'light' | 'dark' | 'system';

const ThemeManager = (function() {
  let instance: any;
  let currentTheme: Theme = 'light';
  const subscribers: ((theme: string) => void)[] = [];

  function createInstance() {
    return {
      getTheme: function(): Theme {
        return currentTheme;
      },
      setTheme: function(newTheme: Theme): void {
        if (currentTheme !== newTheme) {
          console.log(`Setting theme from '${currentTheme}' to '${newTheme}'`);
          currentTheme = newTheme;
          subscribers.forEach(callback => callback(currentTheme));
        }
      },
      subscribe: function(callback: (theme: string) => void): void {
        if (!subscribers.includes(callback)) {
          subscribers.push(callback);
          console.log('Subscriber added. Current subscribers:', subscribers.length);
        }
      },
      unsubscribe: function(callback: (theme: string) => void): void {
        const index = subscribers.indexOf(callback);
        if (index > -1) {
          subscribers.splice(index, 1);
          console.log('Subscriber removed. Current subscribers:', subscribers.length);
        }
      }
    };
  }

  return {
    getManager: function(): any {
      if (!instance) {
        instance = createInstance();
        console.log('ThemeManager instance created.');
      }
      return instance;
    }
  };
})();

// --- Demonstration Usage ---
console.log('--- ThemeManager Demonstration ---');
const manager1 = ThemeManager.getManager();
const manager2 = ThemeManager.getManager();

console.log('\nAre manager1 and manager2 the same instance?', manager1 === manager2); // Expected: true

console.log('\nCurrent theme:', manager1.getTheme()); // Expected: light

const sub1 = (theme: string) => console.log('Subscriber 1 notified:', theme);
manager1.subscribe(sub1);
const sub2 = (theme: string) => console.log('Subscriber 2 notified:', theme);
manager2.subscribe(sub2);

console.log('\nSetting theme to dark via manager1...');
manager1.setTheme('dark'); // Both subscribers should be notified

console.log('\nCurrent theme:', manager2.getTheme()); // Expected: dark

manager1.unsubscribe(sub2);
console.log('\nSetting theme to system via manager2 (sub2 unsubscribed)...');
manager2.setTheme('system'); // Only sub1 should be notified

console.log('\nSetting theme to light (no change, no notification)...');
manager1.setTheme('system'); // No change, no notification

console.log('\nSetting theme to light again...');
manager1.setTheme('light');

