interface ClickableElement {
  element: HTMLElement; // The actual DOM element
  onClick(): void;
}

// Base Component: A simple div with a click handler
class DecoratedDiv implements ClickableElement {
  public element: HTMLDivElement;
  private id: string;

  constructor(id: string = `div-${Math.random().toString(36).substr(2, 9)}`) {
    this.id = id;
    this.element = document.createElement('div');
    this.element.id = this.id;
    this.element.style.width = '120px'; // Slightly larger for better visibility
    this.element.style.height = '120px';
    this.element.style.border = '1px solid black';
    this.element.style.margin = '10px';
    this.element.style.padding = '5px';
    this.element.style.textAlign = 'center';
    this.element.style.lineHeight = '110px';
    this.element.textContent = `Div ${this.id}`;
    document.body.appendChild(this.element);

    // Basic click listener to show original behavior
    this.element.addEventListener('click', () => this.onClick());
  }

  onClick(): void {
    console.log(`Original onClick for Div ID: ${this.id}`);
  }

  // Method to get the div's ID, useful for decorators
  getId(): string {
    return this.id;
  }
}

// Decorator: Adds logging to onClick
function ClickLogger(target: DecoratedDiv): void {
  const originalOnClick = target.onClick;
  target.onClick = function(...args: any[]): void {
    console.log(`[LOG]: Div with ID ${target.getId()} was clicked.`);
    originalOnClick.apply(this, args); // Use originalOnClick from closure, apply 'this' context
  };
}

// Bonus Decorator: Adds styling
function StylingDecorator(target: DecoratedDiv, backgroundColor: string, borderColor: string, borderWidth: string = '2px', borderStyle: string = 'solid'): void {
  const originalRender = target.element.style.backgroundColor; // Capture original style if needed
  target.element.style.backgroundColor = backgroundColor;
  target.element.style.border = `${borderWidth} ${borderStyle} ${borderColor}`;
  target.element.textContent = `Div ${target.getId()} (Styled)`;
}

