class Coffee {
  constructor() {
    this.price = 5;
    this.description = 'Basic Coffee';
  }

  cost() {
    return this.price;
  }

  getDescription() {
    return this.description;
  }
}

function Milk(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 1; // Add milk cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Milk';
  };
  return coffee;
}

function Whip(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 2; // Add whip cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Whip';
  };
  return coffee;
}

function Vanilla(coffee) {
  const baseCost = coffee.cost();
  const baseDescription = coffee.getDescription();

  coffee.cost = function() {
    return baseCost + 3; // Add vanilla cost
  };

  coffee.getDescription = function() {
    return baseDescription + ', Vanilla';
  };
  return coffee;
}

const myCoffee = new Coffee();
console.log(`Base: ${myCoffee.getDescription()} - $${myCoffee.cost()}`); // Base: Basic Coffee - $5

const milkCoffee = Milk(myCoffee);
console.log(`Milk: ${milkCoffee.getDescription()} - $${milkCoffee.cost()}`); // Milk: Basic Coffee, Milk - $6

const whippedMilkCoffee = Whip(milkCoffee);
console.log(`Whipped Milk: ${whippedMilkCoffee.getDescription()} - $${whippedMilkCoffee.cost()}`); // Whipped Milk: Basic Coffee, Milk, Whip - $8

const finalCoffee = Vanilla(whippedMilkCoffee);
console.log(`Final: ${finalCoffee.getDescription()} - $${finalCoffee.cost()}`); // Final: Basic Coffee, Milk, Whip, Vanilla - $11

// Observer Pattern
class Subject {
  constructor() {
    this.observers = [];
  }

  subscribe(observer) {
    if (this.observers.includes(observer)) {
      console.log('Observer already subscribed.');
      return;
    }
    this.observers.push(observer);
    console.log(`${observer.name} subscribed.`);
  }

  unsubscribe(observer) {
    const index = this.observers.indexOf(observer);
    if (index > -1) {
      this.observers.splice(index, 1);
      console.log(`${observer.name} unsubscribed.`);
    } else {
      console.log('Observer not found.');
    }
  }

  notify(data) {
    console.log(`
Subject notifying all observers with data: ${data}`);
    this.observers.forEach(observer => observer.update(data));
  }
}

class Observer {
  constructor(name) {
    this.name = name;
  }

  update(data) {
    console.log(`${this.name} received: ${data}`);
  }
}

const newsPublisher = new Subject();

const subscriber1 = new Observer('John');
const subscriber2 = new Observer('Alice');
const subscriber3 = new Observer('Bob');

newsPublisher.subscribe(subscriber1);
newsPublisher.subscribe(subscriber2);

newsPublisher.notify('Breaking News: New JavaScript Framework Released!');
// Output:
// John subscribed.
// Alice subscribed.
// Subject notifying all observers with data: Breaking News: New JavaScript Framework Released!
// John received: Breaking News: New JavaScript Framework Released!
// Alice received: Breaking News: New JavaScript Framework Released!

newsPublisher.subscribe(subscriber3);
newsPublisher.notify('Sports Update: Local Team Wins Championship!');
// Output:
// Bob subscribed.
// Subject notifying all observers with data: Sports Update: Local Team Wins Championship!
// John received: Sports Update: Local Team Wins Championship!
// Alice received: Sports Update: Local Team Wins Championship!
// Bob received: Sports Update: Local Team Wins Championship!

newsPublisher.unsubscribe(subscriber1);
newsPublisher.notify('Weather Alert: Heavy Rain Expected!');
// Output:
// John unsubscribed.
// Subject notifying all observers with data: Weather Alert: Heavy Rain Expected!
// Alice received: Weather Alert: Heavy Rain Expected!
// Bob received: Weather Alert: Heavy Rain Expected!

// Strategy Pattern

// Base Strategy Interface/Class
class ShippingStrategy {
  calculate(order) {
    throw new Error("Abstract method 'calculate' must be implemented");
  }
}

// Concrete Strategies
class FedEx extends ShippingStrategy {
  calculate(order) {
    console.log(`Calculating FedEx cost for order weight ${order.weight}`);
    return order.weight * 3;
  }
}

class UPS extends ShippingStrategy {
  calculate(order) {
    console.log(`Calculating UPS cost for order weight ${order.weight}`);
    return order.weight * 4;
  }
}

class USPS extends ShippingStrategy {
  calculate(order) {
    console.log(`Calculating USPS cost for order weight ${order.weight}`);
    return order.weight * 2.5;
  }
}

// Context Class
class Shipping {
  constructor() {
    this.strategy = null;
  }

  setStrategy(strategy) {
    if (!(strategy instanceof ShippingStrategy)) {
      throw new Error("Strategy must be an instance of ShippingStrategy.");
    }
    this.strategy = strategy;
    console.log(`Shipping strategy set to ${strategy.constructor.name}`);
  }

  calculate(order) {
    if (!this.strategy) {
      throw new Error("No shipping strategy set.");
    }
    return this.strategy.calculate(order);
  }
}

// Usage
const order1 = { weight: 10, destination: 'NYC' };
const order2 = { weight: 5, destination: 'LA' };

const shipper = new Shipping();

// Calculate with FedEx
shipper.setStrategy(new FedEx());
console.log('Order 1 FedEx Cost:', shipper.calculate(order1)); // Output: 30
console.log('Order 2 FedEx Cost:', shipper.calculate(order2)); // Output: 15

// Calculate with UPS
shipper.setStrategy(new UPS());
console.log('Order 1 UPS Cost:', shipper.calculate(order1));   // Output: 40
console.log('Order 2 UPS Cost:', shipper.calculate(order2));   // Output: 20

// Calculate with USPS
shipper.setStrategy(new USPS());
console.log('Order 1 USPS Cost:', shipper.calculate(order1)); // Output: 25
console.log('Order 2 USPS Cost:', shipper.calculate(order2)); // Output: 12.5

