class EventBus {
  constructor() {
    this.subscribers = {}; // Stores eventName -> [callbacks]
  }

  subscribe(eventName, callback) {
    if (typeof callback !== 'function') {
      console.error('Callback must be a function.');
      return;
    }
    if (!this.subscribers[eventName]) {
      this.subscribers[eventName] = [];
    }
    if (!this.subscribers[eventName].includes(callback)) {
      this.subscribers[eventName].push(callback);
      console.log(`Subscribed to '${eventName}'`);
    } else {
      console.log(`Already subscribed to '${eventName}'.`);
    }
  }

  unsubscribe(eventName, callback) {
    if (!this.subscribers[eventName]) {
      console.log(`No subscribers for '${eventName}'.`);
      return;
    }
    this.subscribers[eventName] = this.subscribers[eventName].filter(cb => cb !== callback);
    console.log(`Unsubscribed from '${eventName}'.`);
  }

  publish(eventName, data) {
    if (!this.subscribers[eventName] || this.subscribers[eventName].length === 0) {
      console.log(`No active subscribers for '${eventName}'.`);
      return;
    }
    console.log(`
Publishing event: '${eventName}' with data: ${JSON.stringify(data)}`);
    this.subscribers[eventName].forEach(callback => {
      try {
        callback(data);
      } catch (error) {
        console.error(`Error in subscriber for '${eventName}':`, error);
      }
    });
  }
}

