class Subject {
  constructor(name) {
    this.name = name; // Added name for clarity in example
    this.observers = [];
  }

  subscribe(observer) {
    if (this.observers.includes(observer)) {
      console.log(`Observer already subscribed to ${this.name}.`);
      return;
    }
    this.observers.push(observer);
    console.log(`${observer.name} subscribed to ${this.name}.`);
  }

  unsubscribe(observer) {
    const index = this.observers.indexOf(observer);
    if (index > -1) {
      this.observers.splice(index, 1);
      console.log(`${observer.name} unsubscribed from ${this.name}.`);
    }
  }

  notify(data) {
    console.log(`
${this.name} notifying all observers with data: ${data}`);
    // Pass subject name along with data for observer to identify source
    this.observers.forEach(observer => observer.update(this.name, data));
  }
}

