// Strategy Interface/Base Class
class DiscountStrategy {
  applyDiscount(total) {
    throw new Error("Abstract method 'applyDiscount' must be implemented");
  }
}

// Concrete Strategy 1: Percentage Discount
class PercentageDiscount extends DiscountStrategy {
  constructor(percentage) {
    super();
    this.percentage = percentage; // e.g., 0.10 for 10%
  }

  applyDiscount(total) {
    if (total < 0) return 0; // Handle negative total if necessary
    const discountAmount = total * this.percentage;
    console.log(`Applying ${this.percentage * 100}% discount: -${discountAmount.toFixed(2)}`);
    return total - discountAmount;
  }
}

// Concrete Strategy 2: Fixed Amount Discount
class FixedAmountDiscount extends DiscountStrategy {
  constructor(amount) {
    super();
    this.amount = amount; // e.g., 5 for $5
  }

  applyDiscount(total) {
    if (total < 0) return 0; // Handle negative total if necessary
    const finalTotal = total - this.amount;
    console.log(`Applying fixed amount discount: -${this.amount.toFixed(2)}`);
    return Math.max(0, finalTotal); // Ensure total doesn't go below zero
  }
}

// Context Class: ShoppingCart
class ShoppingCart {
  constructor() {
    this.items = [];
    this.discountStrategy = null; // No discount by default
  }

  addItem(name, price) {
    this.items.push({ name, price });
  }

  calculateTotal() {
    return this.items.reduce((sum, item) => sum + item.price, 0);
  }

  setDiscountStrategy(strategy) {
    if (!(strategy instanceof DiscountStrategy)) {
      throw new Error("Provided strategy must be an instance of DiscountStrategy.");
    }
    this.discountStrategy = strategy;
    console.log(`Discount strategy set to ${strategy.constructor.name}.`);
  }

  getFinalPrice() {
    let total = this.calculateTotal();
    if (this.discountStrategy) {
      total = this.discountStrategy.applyDiscount(total);
    }
    return total;
  }
}

// --- Demonstration ---
const cart = new ShoppingCart();
cart.addItem('Laptop', 1000);
cart.addItem('Mouse', 25);

console.log(`Initial Total: $${cart.calculateTotal()}`); // Initial Total: $1025

// Apply 10% percentage discount
const tenPercentOff = new PercentageDiscount(0.10);
cart.setDiscountStrategy(tenPercentOff);
console.log(`Price with 10% off: $${cart.getFinalPrice().toFixed(2)}`); // Price with 10% off: $922.50

// Apply $50 fixed amount discount
const fiftyDollarsOff = new FixedAmountDiscount(50);
cart.setDiscountStrategy(fiftyDollarsOff);
console.log(`Price with $50 off: $${cart.getFinalPrice().toFixed(2)}`); // Price with $50 off: $975.00

// Remove discount
cart.setDiscountStrategy(null); // Or provide a NoDiscount strategy
console.log(`Price without discount: $${cart.getFinalPrice().toFixed(2)}`); // Price without discount: $1025.00
