class Button {
  constructor(text) {
    this.text = text;
    this.classes = [];
    this.attributes = [];
    this.icon = '';
  }

  render() {
    const classAttr = this.classes.length > 0 ? ` class="${this.classes.join(' ')}"` : '';
    const otherAttrs = this.attributes.length > 0 ? ` ${this.attributes.join(' ')}` : '';
    const iconHtml = this.icon ? `<i class="${this.icon}"></i> ` : '';
    return `<button${classAttr}${otherAttrs}>${iconHtml}${this.text}</button>`;
  }
}

function DangerButton(button) {
  const originalRender = button.render;
  const originalText = button.text;

  button.render = function() {
    if (!this.classes.includes('danger')) {
      this.classes.push('danger');
    }
    this.text = `(Danger) ${originalText}`;
    return originalRender.call(this);
  };
  return button;
}

function DisabledButton(button) {
  const originalRender = button.render;
  const originalText = button.text;

  button.render = function() {
    if (!this.attributes.includes('disabled')) {
      this.attributes.push('disabled');
    }
    this.text = `(Disabled) ${originalText}`;
    return originalRender.call(this);
  };
  return button;
}

function IconButton(button, iconClass) {
  const originalRender = button.render;

  button.render = function() {
    this.icon = iconClass;
    return originalRender.call(this);
  };
  return button;
}

