class EventBus {
  constructor() {
    this.listeners = {};
  }

  on(eventName, listener) {
    if (typeof listener !== 'function') {
      console.warn(`EventBus: Listener for '${eventName}' must be a function.`);
      return;
    }
    if (!this.listeners[eventName]) {
      this.listeners[eventName] = [];
    }
    if (!this.listeners[eventName].includes(listener)) {
      this.listeners[eventName].push(listener);
      // console.log(`[EventBus] Subscribed to '${eventName}' with listener: ${listener.name || 'anonymous'}`);
    } else {
      // console.log(`[EventBus] Listener already subscribed to '${eventName}'.`);
    }
  }

  off(eventName, listener) {
    if (!this.listeners[eventName]) {
      // console.log(`[EventBus] No subscribers for '${eventName}' to unsubscribe from.`);
      return;
    }
    const initialLength = this.listeners[eventName].length;
    this.listeners[eventName] = this.listeners[eventName].filter(cb => cb !== listener);
    if (this.listeners[eventName].length < initialLength) {
      // console.log(`[EventBus] Unsubscribed from '${eventName}' with listener: ${listener.name || 'anonymous'}`);
    } else {
      // console.log(`[EventBus] Listener not found for '${eventName}'.`);
    }
  }

  emit(eventName, data) {
    if (!this.listeners[eventName] || this.listeners[eventName].length === 0) {
      // console.log(`[EventBus] No active subscribers for '${eventName}'.`);
      return;
    }
    // console.log(`\n[EventBus] Emitting event: '${eventName}' with data: ${JSON.stringify(data)}`);
    // Create a copy of the listeners array to prevent issues if a listener unsubscribes itself during iteration
    const listenersToNotify = [...this.listeners[eventName]];
    listenersToNotify.forEach(listener => {
      try {
        listener(data);
      } catch (error) {
        console.error(`[EventBus Error] Listener for '${eventName}' failed:`, error);
      }
    });
  }
}

