// Base Payment Strategy (abstract)
class PaymentStrategy {
  pay(amount) {
    throw new Error("Abstract method 'pay' must be implemented by concrete strategies.");
  }
}

// Concrete Payment Strategy 1: Credit Card
class CreditCardPaymentStrategy extends PaymentStrategy {
  constructor(cardNumber, expiryDate, cvv) {
    super();
    this.cardNumber = cardNumber;
    this.expiryDate = expiryDate;
    this.cvv = cvv;
  }

  pay(amount) {
    console.log(`Processing credit card payment of $${amount.toFixed(2)}.`);
    console.log(`Card Number: **** **** **** ${this.cardNumber.slice(-4)}, Expiry: ${this.expiryDate}`);
    // Simulate actual payment gateway interaction
    if (amount > 0) {
      console.log('Credit card payment successful.');
      return true;
    } else {
      console.error('Credit card payment failed: Invalid amount.');
      return false;
    }
  }
}

// Concrete Payment Strategy 2: PayPal
class PayPalPaymentStrategy extends PaymentStrategy {
  constructor(email) {
    super();
    this.email = email;
  }

  pay(amount) {
    console.log(`Processing PayPal payment of $${amount.toFixed(2)}.`);
    console.log(`PayPal Account: ${this.email}`);
    // Simulate actual payment gateway interaction
    if (amount > 0) {
      console.log('PayPal payment successful.');
      return true;
    } else {
      console.error('PayPal payment failed: Invalid amount.');
      return false;
    }
  }
}

// Concrete Payment Strategy 3: Crypto
class CryptoPaymentStrategy extends PaymentStrategy {
  constructor(walletAddress, currency) {
    super();
    this.walletAddress = walletAddress;
    this.currency = currency;
  }

  pay(amount) {
    console.log(`Processing Crypto (${this.currency}) payment of $${amount.toFixed(2)}.`);
    console.log(`Sending to Wallet Address: ${this.walletAddress}`);
    // Simulate actual payment gateway interaction
    if (amount > 0) {
      console.log('Crypto payment successful. Please wait for confirmation.');
      return true;
    } else {
      console.error('Crypto payment failed: Invalid amount.');
      return false;
    }
  }
}

// PaymentProcessor (Context) class
class PaymentProcessor {
  constructor() {
    this.strategy = null;
  }

  setStrategy(strategy) {
    if (strategy === null) {
      this.strategy = null;
      console.log('Payment strategy cleared.');
      return;
    }
    if (!(strategy instanceof PaymentStrategy)) {
      throw new Error("Invalid strategy: Must be an instance of PaymentStrategy.");
    }
    this.strategy = strategy;
    console.log(`Payment strategy set to: ${strategy.constructor.name}`);
  }

  processPayment(amount) {
    if (!this.strategy) {
      console.error("Error: No payment strategy set. Cannot process payment.");
      return false;
    }
    if (amount <= 0) {
      console.error("Error: Payment amount must be positive.");
      return false;
    }
    return this.strategy.pay(amount);
  }
}

// --- Demonstration Usage ---
