```javascript
// Strategy Interface (implicit)
// class SortStrategy { sort(data) { throw new Error('Method must be implemented'); } }

class BubbleSort {
  sort(data) {
    const arr = [...data]; // Avoid modifying original array
    const n = arr.length;
    for (let i = 0; i < n - 1; i++) {
      for (let j = 0; j < n - i - 1; j++) {
        if (arr[j] > arr[j + 1]) {
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
        }
      }
    }
    return arr;
  }
}

class QuickSort {
  sort(data) {
    const arr = [...data]; // Avoid modifying original array
    if (arr.length <= 1) return arr;

    const pivot = arr[Math.floor(arr.length / 2)];
    const left = [];
    const right = [];
    const equal = [];

    for (let i = 0; i < arr.length; i++) {
      if (arr[i] < pivot) {
        left.push(arr[i]);
      } else if (arr[i] > pivot) {
        right.push(arr[i]);
      } else {
        equal.push(arr[i]);
      }
    }
    return [...this.sort(left), ...equal, ...this.sort(right)];
  }
}

// Context
class Sorter {
  constructor() {
    this.strategy = null;
  }

  setStrategy(strategy) {
    this.strategy = strategy;
  }

  executeSort(data) {
    if (!this.strategy) {
      throw new Error("No sorting strategy set.");
    }
    return this.strategy.sort(data);
  }
}

// Usage
const dataToSort = [64, 25, 12, 22, 11];
const sorter = new Sorter();

sorter.setStrategy(new BubbleSort());
console.log("Bubble Sorted:", sorter.executeSort(dataToSort)); // [11, 12, 22, 25, 64]

sorter.setStrategy(new QuickSort());
console.log("Quick Sorted:", sorter.executeSort(dataToSort)); // [11, 12, 22, 25, 64]

const data2 = [3, 1, 4, 1, 5, 9, 2, 6];
sorter.setStrategy(new BubbleSort());
console.log("Bubble Sorted 2:", sorter.executeSort(data2)); // [1, 1, 2, 3, 4, 5, 6, 9]

sorter.setStrategy(new QuickSort());
console.log("Quick Sorted 2:", sorter.executeSort(data2)); // [1, 1, 2, 3, 4, 5, 6, 9]
```

