tasks.solutionCode:
function deepClone(value, hash = new WeakMap()) {
  // 1. Handle primitives, null, undefined, functions, symbols, BigInts
  // For simplicity, we'll treat functions, Symbols, BigInts as non-clonable objects,
  // meaning they are returned by reference, which is standard for deep cloning.
  // Date and RegExp objects are also tricky. A simple deep clone usually doesn't recreate them.
  if (value === null || typeof value !== 'object') {
    return value;
  }

  // Handle Date objects
  if (value instanceof Date) {
    return new Date(value.getTime());
  }

  // Handle RegExp objects
  if (value instanceof RegExp) {
    return new RegExp(value.source, value.flags);
  }

  // 2. Handle circular references
  if (hash.has(value)) {
    return hash.get(value);
  }

  // 3. Determine if value is an Array or Object and create initial clone
  const clone = Array.isArray(value) ? [] : {};

  // 4. Store the clone in hash BEFORE recursive calls to handle circular references
  hash.set(value, clone);

  // 5. Recursively clone properties/elements
  // Use Object.keys for objects to get enumerable own properties, 
  // or iterate through arrays
  if (Array.isArray(value)) {
    for (let i = 0; i < value.length; i++) {
      clone[i] = deepClone(value[i], hash);
    }
  } else {
    for (const key in value) {
      if (Object.prototype.hasOwnProperty.call(value, key)) {
        clone[key] = deepClone(value[key], hash);
      }
    }
  }

  return clone;
}

// Test cases
