tasks.solutionCode:
function convertToStrictNumber(input) {
  // Rule 1: If the input is already a Number (and not NaN), return it as is.
  // We check for NaN later, as the initial 'typeof number' check would include NaN.
  if (typeof input === 'number' && !Number.isNaN(input)) {
    return input;
  }

  // Rule 2 & 3: If the input is null, undefined, or an empty string, return 0.
  if (input === null || input === undefined || input === '') {
    return 0;
  }

  // Rule 4 & 5: If the input is a string that represents a valid number, convert it.
  // If it contains non-numeric chars, return NaN.
  if (typeof input === 'string') {
    // Use a regular expression to check if the string contains *only* numeric characters
    // including optional sign, decimals, and scientific notation.
    // Trim whitespace first.
    const trimmedInput = input.trim();
    if (trimmedInput === '') return 0; // After trim, if empty, it's an empty string case handled already

    // Regex to match valid number strings including integers, floats, and scientific notation
    // Allows for optional leading +/- sign, digits, optional decimal with digits, optional exponent part
    const numberRegex = /^[+-]?(\d+(\.\d*)?|\.\d+)([eE][+-]?\d+)?$/;

    if (numberRegex.test(trimmedInput)) {
      return Number(trimmedInput);
    } else {
      return NaN; // String contains non-numeric characters
    }
  }

  // Rule 6: If the input is NaN (from initial type check), handle it.
  if (Number.isNaN(input)) {
    return NaN;
  }

  // Rule 7: For any other type of input (e.g., boolean, object, array), return NaN.
  return NaN;
}

// Test cases
