function toBoolean(value, strict = false) {
  if (strict) {
    // Strict mode: Only true if value is strictly boolean true/false
    if (value === true) {
      return true;
    } else if (value === false) {
      return false;
    } else {
      return null; // For any other value, return null
    }
  } else {
    // Standard mode: Use JavaScript's built-in truthiness/falsiness rules
    return !!value;
  }
}

