tasks.solutionCode:
function calculateTotal(price, quantityInput) {
  // Explanation of the bug:
  // The '+' operator in JavaScript performs string concatenation if one of the operands is a string.
  // Here, 'price' is a number and 'quantityInput' is a string ("2").
  // JavaScript coerces 'price' (10.50) to a string ("10.50") and then concatenates it with "2",
  // resulting in "10.502", which is not the intended numeric sum.

  // Correction: Explicitly convert quantityInput to a number.
  // Options: Number(), parseInt(), parseFloat(), or unary plus (+)
  const numericQuantity = Number(quantityInput); // Or +quantityInput; or parseFloat(quantityInput);

  // Now perform the correct numeric addition
  const total = price * numericQuantity; // Using multiplication for clarity on price * quantity
                                        // If the intention was actually price + quantity, then it would be:
                                        // const total = price + numericQuantity;

  console.log(`Calculated Total: ${total}`);
  return total;
}

// Test cases:
console.log("Test 1:");
calculateTotal(10.50, "2"); // Expected: 21.00
console.log("Test 2:");
calculateTotal(5, "3");   // Expected: 15

// Bonus: Handle invalid quantity input
function calculateTotalSafe(price, quantityInput) {
  const numericQuantity = Number(quantityInput);
  if (isNaN(numericQuantity)) {
    console.error("Invalid quantity input: Not a number");
    return NaN; // Or throw an error, or return 0
  }
  const total = price * numericQuantity;
  console.log(`Calculated Total Safe: ${total}`);
  return total;
}

console.log("Test 3 (Safe):");
calculateTotalSafe(10, "abc"); // Expected: NaN (or handled error)
console.log("Test 4 (Safe):");
calculateTotalSafe(7, "0");    // Expected: 0

