const userManager = (function() {
  let _nextId = 1;
  let _users = [];

  return {
    addUser: function(userObject) {
      if (!userObject || typeof userObject !== 'object' || !userObject.name) {
        console.error('Invalid user object. Must have a name.');
        return;
      }
      const newUser = { ...userObject, id: userObject.id || _nextId++ };
      _users.push(newUser);
      console.log(`User added: ${newUser.name} (ID: ${newUser.id})`);
      return newUser;
    },

    getUserById: function(id) {
      return _users.find(user => user.id === id);
    },

    getAllUsers: function() {
      // Return a shallow copy to prevent direct modification of the private array
      return [..._users]; 
    },

    removeUser: function(id) {
      const initialLength = _users.length;
      _users = _users.filter(user => user.id !== id);
      if (_users.length < initialLength) {
        console.log(`User with ID ${id} removed.`);
        return true;
      } else {
        console.warn(`User with ID ${id} not found.`);
        return false;
      }
    }
  };
})();

