class Carousel {
  constructor(slides) {
    this.slides = slides;
    this.currentIndex = 0;

    this.container = document.createElement('div');
    this.container.style.border = '1px solid black';
    this.container.style.padding = '10px';
    this.container.style.margin = '20px';
    this.container.style.textAlign = 'center';

    this.display = document.createElement('div');
    this.display.style.fontSize = '24px';
    this.display.style.minHeight = '50px';
    this.display.style.lineHeight = '50px';
    this.display.style.borderBottom = '1px solid #ccc';
    this.display.style.marginBottom = '10px';

    this.prevBtn = document.createElement('button');
    this.nextBtn = document.createElement('button');

    this.prevBtn.textContent = 'Previous';
    this.nextBtn.textContent = 'Next';
    this.prevBtn.style.marginRight = '10px';

    this.container.appendChild(this.prevBtn);
    this.container.appendChild(this.display);
    this.container.appendChild(this.nextBtn);
    document.body.appendChild(this.container);

    this.updateDisplay();

    // Method 1: Using .bind(this) to ensure 'this' refers to the Carousel instance
    this.nextBtn.addEventListener('click', this.nextSlide.bind(this));
    this.prevBtn.addEventListener('click', this.prevSlide.bind(this));

    // Method 2: Using an arrow function directly in addEventListener
    // this.nextBtn.addEventListener('click', () => this.nextSlide());
    // this.prevBtn.addEventListener('click', () => this.prevSlide());
  }

  updateDisplay() {
    if (this.slides.length === 0) {
      this.display.textContent = 'No slides available.';
      return;
    }
    this.display.textContent = `Slide ${this.currentIndex + 1}: ${this.slides[this.currentIndex]}`;
  }

  nextSlide() {
    // Ensure 'this' refers to the Carousel instance here
    this.currentIndex = (this.currentIndex + 1) % this.slides.length;
    this.updateDisplay();
    console.log('Next clicked, current index:', this.currentIndex);
  }

  prevSlide() {
    // Ensure 'this' refers to the Carousel instance here
    this.currentIndex = (this.currentIndex - 1 + this.slides.length) % this.slides.length;
    this.updateDisplay();
    console.log('Prev clicked, current index:', this.currentIndex);
  }
}

// Example usage:
const mySlides = ['Hello from Slide 1!', 'This is Slide 2.', 'Final Slide 3.'];
const myCarousel = new Carousel(mySlides);

// Test with empty slides
const emptyCarousel = new Carousel([]);

