console.log('Invalid (no @):', isEmail('testexample.com')); // Invalid email format
console.log('Invalid (empty):', isEmail('')); // Invalid email format (because notEmpty is not part of this specific email rule)

console.log('\n--- Chaining Validators (Conceptual) ---');
// In a real system, you'd apply a list of validators to an input
const usernameValidators = [
  isRequired,
  minLength(3),
  maxLength(10)
];

function validateInput(value, validators) {
  for (const validatorFn of validators) {
    const result = validatorFn(value);
    if (result !== true) {
      return result; // Return first error message
    }
  }
  return true; // All good
}

