function memoize(fn) {
  const cache = {};
  return function(...args) {
    // Create a unique key from arguments. JSON.stringify works for simple types.
    // For complex objects, it might not be suitable if order or non-enumerable props matter.
    const key = JSON.stringify(args);
    if (cache[key]) {
      console.log(`Fetching '${key}' from cache`);
      return cache[key];
    }
    console.log(`Calculating result for '${key}'`);
    // Use apply to ensure 'this' context is correctly passed if 'fn' relies on it.
    const result = fn.apply(this, args);
    cache[key] = result;
    return result;
  };
}

const factorial = memoize(function(n) {
  if (n === 0 || n === 1) return 1;
  return n * factorial(n - 1);
});

console.log(factorial(5)); // Calculating result, then 120
