```javascript
function deepSortAndStringify(arg) {
  if (typeof arg !== 'object' || arg === null) {
    return JSON.stringify(arg);
  }
  if (Array.isArray(arg)) {
    return '[' + arg.map(item => deepSortAndStringify(item)).join(',') + ']';
  }
  const sortedKeys = Object.keys(arg).sort();
  const sortedObject = {};
  for (const key of sortedKeys) {
    sortedObject[key] = arg[key];
  }
  return '{' + sortedKeys.map(key => `"${key}":${deepSortAndStringify(sortedObject[key])}`).join(',') + '}';
}

function memoizeComplex(fn) {
  const cache = new Map(); // Using Map for better key handling with complex types potentially
  return function(...args) {
    // Create a robust key for complex arguments
    const key = deepSortAndStringify(args);

    if (cache.has(key)) {
      console.log('Fetching from cache (complex key):', key);
      return cache.get(key);
    }

    console.log('Calculating result (complex key):', key);
    const result = fn.apply(this, args);
    cache.set(key, result);
    return result;
  };
}

// Example usage:
const sumObjects = memoizeComplex(function(obj1, obj2) {
  const sumKeys = [...new Set([...Object.keys(obj1), ...Object.keys(obj2)])];
  const result = {};
  for (const key of sumKeys) {
    result[key] = (obj1[key] || 0) + (obj2[key] || 0);
  }
  return result;
});

console.log(sumObjects({a: 1, b: 2}, {b: 3, c: 4})); // Calculates
console.log(sumObjects({b: 2, a: 1}, {c: 4, b: 3})); // Fetches from cache due to sorted key
console.log(sumObjects({a: 5}, {b: 6})); // Calculates
```

