function memoize(fn) {
  const cache = {};
  return function(arg) {
    // Use a simple string conversion for primitive arguments as key
    const key = String(arg);

    if (cache.hasOwnProperty(key)) {
      console.log(`Fetching fib(${arg}) from cache`);
      return cache[key];
    }

    console.log(`Calculating fib(${arg})`);
    // Note: 'this' context is not critical for standalone fibonacci function,
    // but for completeness in a generic memoize, fn.apply(this, [arg]) could be used.
    const result = fn(arg);
    cache[key] = result;
    return result;
