function* paginate(items, pageSize) {
  if (!Array.isArray(items)) {
    throw new Error('Input items must be an array.');
  }
  if (pageSize <= 0) {
    throw new Error('Page size must be a positive integer.');
  }
  if (items.length === 0) {
    return; // Yield nothing for empty array
  }

  let startIndex = 0;
  while (startIndex < items.length) {
    const page = items.slice(startIndex, startIndex + pageSize);
    yield page;
    startIndex += pageSize;
  }
}

// Example Usage:
console.log('--- Pagination Test 1 (Basic) ---');
const data1 = Array.from({ length: 10 }, (_, i) => `Item ${i + 1}`);
const paginator1 = paginate(data1, 3);
console.log(paginator1.next().value); // ['Item 1', 'Item 2', 'Item 3']
console.log(paginator1.next().value); // ['Item 4', 'Item 5', 'Item 6']
console.log(paginator1.next().value); // ['Item 7', 'Item 8', 'Item 9']
console.log(paginator1.next().value); // ['Item 10']
console.log(paginator1.next().done);  // true

console.log('\n--- Pagination Test 2 (Exact Fit) ---');
