async function fetchProducts(category) {
  console.log(`Fetching products for category: ${category}...`);
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      if (category === 'electronics') {
        resolve([
          { id: 'p1', name: 'Laptop', price: 1200 },
          { id: 'p2', name: 'Mouse', price: 25 },
          { id: 'p3', name: 'Keyboard', price: 75 }
        ]);
      } else if (category === 'books') {
        resolve([
          { id: 'b1', name: 'The Great Gatsby', price: 15 }
        ]);
      } else if (category === 'fail-products') {
        reject(new Error('Simulated network error fetching products.'));
      } else {
        resolve([]); // Empty array for unknown categories
      }
    }, 500);
  });
}

async function fetchReviews(productId) {
  console.log(`Fetching reviews for product: ${productId}...`);
  return new Promise((resolve, reject) => {
    const delay = Math.random() * 300 + 100; // Random delay between 100-400ms
    setTimeout(() => {
      if (productId === 'p1') {
        resolve([
          { id: 'r1', text: 'Great laptop' },
          { id: 'r2', text: 'Fast shipping' }
        ]);
      } else if (productId === 'p2') {
        // Simulate a failure for a specific product's reviews
        reject(new Error(`Simulated error fetching reviews for ${productId}`));
      } else if (productId === 'b1') {
        resolve([]); // No reviews for books
      } else if (productId === 'p3') {
         resolve([{ id: 'r3', text: 'Nice keyboard' }]);
      } else {
        resolve([]); // No reviews for other products
      }
    }, delay);
  });
}

async function getProductsWithReviews(category) {
  console.log(`\n--- Starting aggregation for category: ${category} ---`);
  let products = [];
  try {
    products = await fetchProducts(category);
  } catch (error) {
    console.error(`ERROR: Failed to fetch products for category '${category}':`, error.message);
    return []; // If products fetch fails, return empty array
  }

  // If no products, no need to fetch reviews
  if (products.length === 0) {
    console.log(`No products found for category: ${category}. Returning empty.`);
    return [];
  }

  // Map each product to a promise for its reviews
  // Use .then().catch() on individual promises to prevent Promise.all from failing fast
  const reviewPromises = products.map(async product => {
    try {
      const reviews = await fetchReviews(product.id);
      return { ...product, reviews };
    } catch (error) {
      console.error(`WARNING: Failed to fetch reviews for product ${product.id}:`, error.message);
      // If review fetch fails, include product but with empty reviews array and an error flag
      return { ...product, reviews: [], reviewError: error.message }; 
    }
  });

  // Await all review promises concurrently
  // Promise.all is used here because individual errors are caught and handled already
  const productsWithReviews = await Promise.all(reviewPromises);

  console.log(`--- Finished aggregation for category: ${category} ---`);
  return productsWithReviews;
}

// Example Usage:
(async () => {
  const electronicsData = await getProductsWithReviews('electronics');
  console.log('Result (Electronics):', JSON.stringify(electronicsData, null, 2));

