class Counter {
  constructor(initialCount = 0) {
    this.count = initialCount;
    this.intervalId = null;
  }

  display() {
    console.log(`Current count: ${this.count}`);
  }

  start() {
    console.log('Starting counter...');
    // Fix: Use an arrow function for the setInterval callback.
    // The arrow function lexically binds 'this' from the 'start' method's scope,
    // which correctly refers to the Counter instance.
    this.intervalId = setInterval(() => {
      this.count++; 
      this.display(); 
    }, 1000);
  }

  stop() {
    if (this.intervalId) {
      clearInterval(this.intervalId);
      console.log('Counter stopped.');
      this.intervalId = null;
    }
  }
}

