  // `key.split('.')` splits the string 'a.b.c' into ['a', 'b', 'c']
  // `reduce` then iteratively accesses the nested properties
  return key.split('.').reduce((accum, currentKey) => {
    // Ensure accum is not null/undefined before accessing its property
    return (accum && typeof accum === 'object' && accum.hasOwnProperty(currentKey)) ? accum[currentKey] : undefined;
  }, obj);
