  // `reduceRight` would achieve left-to-right if preferred for pipelines
  return fns.reduce((accum, nextFn) => nextFn(accum), res);
}

const add5 = x => x + 5;
const multiplyBy2 = x => x * 2;
const subtract3 = x => x - 3;

// Applying functions sequentially from left to right: subtract3(multiplyBy2(add5(initialValue)))
const operate = compose(add5, multiplyBy2, subtract3);

// If initialValue = 10:
// 1. add5(10) -> 15
// 2. multiplyBy2(15) -> 30
// 3. subtract3(30) -> 27
console.log(operate(10)); // 27

const toUpperCase = str => str.toUpperCase();
const addExclamation = str => str + '!';
const reverseString = str => str.split('').reverse().join('');

