// If initialValue = 'hello':
// 1. reverseString('hello') -> 'olleh'
// 2. toUpperCase('olleh') -> 'OLLEH'
// 3. addExclamation('OLLEH') -> 'OLLEH!'
console.log(transformText('hello')); // 'OLLEH!'

// Parent constructor function
var Person = function(firstName) {
  this.firstName = firstName;
};

// Methods shared by all Person instances are added to Person.prototype
Person.prototype.sayHello = function() {
  console.log("Hello, I'm " + this.firstName);
};

// Child constructor function
function Student(firstName, subject) {
  // 1. Call parent constructor to inherit parent's properties on 'this' (the Student instance)
  Person.call(this, firstName);
  this.subject = subject;
}

// 2. Set Student's prototype to an object that inherits from Person's prototype
// This establishes the prototype chain: studentInstance -> Student.prototype -> Person.prototype -> Object.prototype
Student.prototype = Object.create(Person.prototype);

// 3. Reset the constructor property, as Object.create sets it to Person
Student.prototype.constructor = Student;

// 4. Add methods specific to Student
Student.prototype.saySubject = function() {
  console.log("I study " + this.subject);
};

// Create instances
var person1 = new Person("Alice");
var student1 = new Student("Bob", "Math");

person1.sayHello(); // "Hello, I'm Alice"
student1.sayHello(); // "Hello, I'm Bob" (inherited from Person.prototype)
student1.saySubject(); // "I study Math" (specific to Student.prototype)

console.log(student1 instanceof Student); // true
console.log(student1 instanceof Person); // true
console.log(student1.constructor === Student); // true

