```javascript
// Vehicle Constructor
var Vehicle = function(make, model) {
  this.make = make;
  this.model = model;
};

// Vehicle Prototype Methods
Vehicle.prototype.getInfo = function() {
  return 'Make: ' + this.make + ', Model: ' + this.model;
};

// Car Constructor
function Car(make, model, numDoors) {
  // 1. Constructor borrowing: Call the parent constructor to inherit properties
  Vehicle.call(this, make, model);
  this.numDoors = numDoors;
}

// 2. Set up the prototype chain: Car.prototype inherits from Vehicle.prototype
// This allows Car instances to access methods defined on Vehicle.prototype
Car.prototype = Object.create(Vehicle.prototype);

// 3. Correct the constructor property for Car.prototype
// Without this, Car.prototype.constructor would point to Vehicle
Car.prototype.constructor = Car;

// 4. Add methods specific to Car on its prototype
Car.prototype.getDoors = function() {
  return 'Number of doors: ' + this.numDoors;
};

// Expected Usage:
