class MathUtil {
  constructor(initialValue = 0) {
    this.currentValue = initialValue;
  }

  add(num) {
    this.currentValue += num;
    return this;
  }

  subtract(num) {
    this.currentValue -= num;
    return this;
  }

  multiply(num) {
    this.currentValue *= num;
    return this;
  }

  divide(num) {
    if (num === 0) {
      throw new Error("Division by zero is not allowed.");
    }
    this.currentValue /= num;
    return this;
  }

  reset() {
    this.currentValue = 0;
    return this;
  }

  equals() {
    return this.currentValue;
  }
}

