tasks.solutionCode:
function createDataTransformer(...transforms) {
  return function(data) {
    let currentResult = data;

    for (const transformFn of transforms) {
      // If currentResult is null or undefined, stop applying further transformations
      if (currentResult === null || typeof currentResult === 'undefined') {
        return currentResult;
      }
      currentResult = transformFn(currentResult);
    }
    return currentResult;
  };
}

// Example Transformations:
