  this.radius = radius;
}

// Set up the prototype chain: Circle.prototype inherits from Shape.prototype
// This allows Circle instances to access methods from Shape.prototype
Circle.prototype = Object.create(Shape.prototype);

// Crucial: Correct the constructor property for Circle.prototype
// Without this, Circle instances would appear to be constructed by Shape
Circle.prototype.constructor = Circle;

// Circle Prototype Method
Circle.prototype.getArea = function() {
  return Math.PI * this.radius * this.radius;
};

// Example Usage:
var redCircle = new Circle('red', 5);
console.log(redCircle.getColor());  // Expected: 'red'
console.log(redCircle.getArea());   // Expected: 78.53981633974483
console.log(redCircle instanceof Circle);  // Expected: true
console.log(redCircle instanceof Shape);   // Expected: true
console.log(redCircle.constructor === Circle); // Expected: true

var blueShape = new Shape('blue');
console.log(blueShape.getColor()); // Expected: 'blue'
console.log(blueShape instanceof Circle); // Expected: false
console.log(blueShape instanceof Shape); // Expected: true

