  this.title = title;
  this.isBorrowed = false;
}

LibraryItem.prototype.borrow = function() {
  if (!this.isBorrowed) {
    this.isBorrowed = true;
    return `Item ${this.title} (ID: ${this.id}) borrowed.`;
  }
  return `Item ${this.title} (ID: ${this.id}) is already borrowed.`;
