```javascript
const userList = [
  { id: 1, name: 'Alice', isActive: true },
  { id: 2, name: 'Bob', isActive: false },
  { id: 3, name: 'Charlie', isActive: true },
];

/**
 * Updates the 'isActive' status of a user in the userList immutably.
 * @param {Array<Object>} users - The original array of user objects.
 * @param {number} userId - The ID of the user to update.
 * @param {boolean} newStatus - The new isActive status.
 * @returns {Array<Object>} A new array with the updated user object, or the original array if user not found.
 */
function updateUserStatus(users, userId, newStatus) {
  const userIndex = users.findIndex(user => user.id === userId);

  if (userIndex === -1) {
    return users; // User not found, return original array (immutably)
  }

  // Create a new user object with updated status (immutably)
  const updatedUser = {
    ...users[userIndex],
    isActive: newStatus,
  };

  // Create a new array with the updated user at the correct index (immutably)
  return [
    ...users.slice(0, userIndex),
    updatedUser,
    ...users.slice(userIndex + 1),
  ];
}

// Test Cases:
const updatedList1 = updateUserStatus(userList, 2, true);
console.log("Original list user 2 active (should be false):", userList[1].isActive);       // Expected: false (original unchanged)
console.log("Updated list user 2 active (should be true):", updatedList1[1].isActive);    // Expected: true (new list updated)
console.log("Original array reference === new array reference (should be false):", userList === updatedList1); // Expected: false (new array created)
console.log("Unaffected user 1 object reference === new list user 1 object reference (should be true):", userList[0] === updatedList1[0]); // Expected: true (unaffected objects share reference for performance)
console.log("Unaffected user 3 object reference === new list user 3 object reference (should be true):", userList[2] === updatedList1[2]); // Expected: true

const updatedList2 = updateUserStatus(userList, 99, true); // User not found
console.log("User not found, array reference === original array reference (should be true):", updatedList2 === userList); // Expected: true (original array returned)

