  takeDamage: (amount) => {
    state.health = Math.max(0, state.health - amount);
    console.log(`${state.name} takes ${amount} damage. Health: ${state.health}`);
  },
});

const canHeal = (state) => ({
  heal: (target, amount) => {
    if (target && target.health !== undefined) { 
      const oldHealth = target.health;
      target.health = Math.min(100, target.health + amount);
      console.log(`${state.name} heals ${target.name} for ${amount} health. Health: ${oldHealth} -> ${target.health}`);
    } else {
      console.log(`${state.name} cannot heal this target.`);
    }
  },
});

