function createConfigManager(initialConfig) {
  const deepClone = (obj) => {
    try {
      return structuredClone(obj);
    } catch (e) {
      return JSON.parse(JSON.stringify(obj)); 
    }
  };

  const privateInitialConfig = deepClone(initialConfig); 
  let currentConfig = deepClone(privateInitialConfig); 

  const createNewManager = (config) => createConfigManager(config);

  return {
    getConfig() {
      return { ...currentConfig }; 
    },

    updateConfig(newSettings) {
      let updatedNotifications = currentConfig.notifications;
      if (newSettings.notifications) {
        updatedNotifications = { ...currentConfig.notifications, ...newSettings.notifications };
      }

      const newMergedConfig = {
        ...currentConfig,
        ...newSettings,
        ...(newSettings.notifications !== undefined ? { notifications: updatedNotifications } : {}),
      };

      return createNewManager(newMergedConfig);
    },

    resetConfig() {
      return createNewManager(privateInitialConfig);
    },
  };
}

// Example Usage & Tests:
const defaultSettings = {
  theme: 'dark',
  language: 'en',
  notifications: { email: true, sms: false },
  user: { id: 1, name: 'Guest' }
};

let manager1 = createConfigManager(defaultSettings);
console.log('Manager 1 initial:', manager1.getConfig());

const config1 = manager1.getConfig();
config1.theme = 'light'; 
console.log('Manager 1 after direct mutation attempt (theme should be dark):', manager1.getConfig().theme); 

let manager2 = manager1.updateConfig({ theme: 'light', language: 'fr' });
console.log('Manager 2 theme:', manager2.getConfig().theme); 
console.log('Manager 2 language:', manager2.getConfig().language); 
console.log('Manager 1 theme (should be original):', manager1.getConfig().theme); 
console.log('Manager 1 === Manager 2 (should be false):', manager1 === manager2); 

let manager3 = manager2.updateConfig({ notifications: { email: false, push: true } });
console.log('Manager 3 email notifications:', manager3.getConfig().notifications.email); 
console.log('Manager 3 SMS notifications (should be original false):', manager3.getConfig().notifications.sms); 
console.log('Manager 3 Push notifications (new):', manager3.getConfig().notifications.push); 
console.log('Manager 2 notifications === Manager 3 notifications (should be false due to nested update):', manager2.getConfig().notifications === manager3.getConfig().notifications); 
console.log('Manager 2 user === Manager 3 user (should be true for unchanged nested object):', manager2.getConfig().user === manager3.getConfig().user); 

let manager4 = manager3.resetConfig();
console.log('Manager 4 theme (should be default):', manager4.getConfig().theme); 
