tasks.solutionCode:
function createArrayGenerator() {
  return {
    /**
     * Generates an array of numbers from start to end (inclusive) with a step.
     * @param {number} start
     * @param {number} end
     * @param {number} [step=1]
     * @returns {number[]}
     */
    range(start, end, step = 1) {
      if (step === 0) return [];
      if (start > end && step > 0) return [];
      if (start < end && step < 0) return [];

      const result = [];
      if (step > 0) {
        for (let i = start; i <= end; i += step) {
          result.push(i);
        }
      } else { 
        for (let i = start; i >= end; i += step) {
          result.push(i);
        }
      }
      return result;
    },

    /**
     * Creates a new array from an iterable object, with an optional map function.
     * @param {Iterable<any>} iterable
     * @param {Function} [mapFn=null]
     * @returns {Array<any>}
     */
    fromIterable(iterable, mapFn = null) {
      try {
        if (iterable && typeof iterable[Symbol.iterator] === 'function') {
          return Array.from(iterable, mapFn);
        } else {
          console.warn('Input is not iterable for fromIterable:', iterable);
          return []; 
        }
      } catch (e) {
        console.error('Error creating array from iterable:', e);
        return [];
      }
    },

    /**
     * Creates an array from a variable number of arguments.
     * @param {...any} values
     * @returns {Array<any>}
     */
    ofValues(...values) {
      return Array.of(...values);
    },

    /**
     * Creates an array of a specified length, filled with a given value.
     * @param {number} length
     * @param {any} fillValue
     * @returns {Array<any>}
     */
    filled(length, fillValue) {
      if (length <= 0) {
        return [];
      }
      return Array.from({ length }, () => fillValue);
    },
  };
}

