function processArray(arr) {
  if (!Array.isArray(arr) || arr.length < 5) {
    // Handle cases where array is too short for slice(0,3) and slice(-2)
    // Or simply define expected behavior for short arrays.
    return arr.slice(0, Math.min(arr.length, 3)).concat(100, arr.slice(Math.max(0, arr.length - 2)));
  }
  return arr.slice(0, 3).concat(100, arr.slice(-2));
}

const originalArray = [1, 2, 3, 4, 5, 6, 7];
const newArray = processArray(originalArray);

