class TodoList {
  constructor() {
    this.tasks = [];
  }

  addTask(task) {
    return this.tasks.push(task);
  }

  removeTask(task) {
    const index = this.tasks.indexOf(task);
    if (index > -1) {
      this.tasks.splice(index, 1);
      return true;
    }
    return false;
  }

  getTasks() {
    // Return a shallow copy to prevent direct mutation of the internal array
    return this.tasks.slice(); 
  }

  clearTasks() {
    // Option 1: Reassigning an empty array (common, effectively non-mutating for the *reference* but clears contents)
    // this.tasks = []; 

    // Option 2: Mutating the array to empty it in-place (if the reference must remain the same)
    this.tasks.splice(0, this.tasks.length); 
  }
}

// Example Usage:
