tasks.solutionCode:
function prepend(arr, element) {
  // Use concat or spread syntax
  return [element].concat(arr);
  // return [element, ...arr];
}

function append(arr, element) {
  // Use concat or spread syntax
  return arr.concat(element);
  // return [...arr, element];
}

function removeAtIndex(arr, index) {
  if (!Array.isArray(arr) || index < 0 || index >= arr.length) {
    return arr; // Return original array if index is out of bounds or invalid input
  }
  // Use slice to get parts before and after the removed element, then concat
  return arr.slice(0, index).concat(arr.slice(index + 1));
}

function insertAtIndex(arr, index, element) {
  if (!Array.isArray(arr)) {
    return arr; // Return original array if invalid input
  }
  // Normalize index for inserting at beginning or end if out of bounds
  const normalizedIndex = Math.max(0, Math.min(index, arr.length));

  // Use slice to get parts, then concat with the new element in between
  return arr.slice(0, normalizedIndex).concat(element, arr.slice(normalizedIndex));
}

