interface Product {
  id: number;
  name: string;
  category: string;
  price: number;
  inStock: boolean;
}

function findProductById(products: Product[], id: number): Product | undefined {
  return products.find(product => product.id === id);
}

function filterProductsByCategory(products: Product[], category: string): Product[] {
  return products.filter(product => product.category === category);
}

function checkProductAvailability(products: Product[], productName: string): boolean {
  const lowerCaseProductName = productName.toLowerCase();
  return products.some(product => 
    product.name.toLowerCase() === lowerCaseProductName && product.inStock
  );
}

function getIndexOfExpensiveProduct(products: Product[], minPrice: number): number {
  return products.findIndex(product => product.price >= minPrice);
}

