tasks.solutionCode:
function validateOrderItems(availableItems: string[], orderItems: string[]): boolean {
  if (!Array.isArray(availableItems) || !Array.isArray(orderItems)) {
    throw new Error('Inputs must be arrays.');
  }

  // Convert availableItems to a Set of lowercase names for efficient lookup
  // Using a Set provides O(1) average time complexity for lookups, better than O(N) for indexOf/includes on arrays
  const availableItemsLowerCaseSet = new Set(availableItems.map(item => item.toLowerCase()));

  // Check if every ordered item is present in the available items (case-insensitive)
  return orderItems.every(orderItem => 
    availableItemsLowerCaseSet.has(orderItem.toLowerCase())
  );
}

// Test Cases:
const available = ['Apple', 'Banana', 'Orange', 'Grape'];

console.log('Valid order:', validateOrderItems(available, ['apple', 'Orange'])); // true
console.log('Invalid order (Pear not available):', validateOrderItems(available, ['apple', 'Pear'])); // false
console.log('Empty order:', validateOrderItems(available, [])); // true (all 0 items are available)
console.log('Empty available:', validateOrderItems([], ['apple'])); // false
console.log('Case-insensitive match:', validateOrderItems(available, ['APPLE', 'Grape'])); // true
console.log('Order with duplicate:', validateOrderItems(available, ['Apple', 'Apple'])); // true

