tasks.solutionCode:
interface Sale {
  product: string;
  quantity: number;
  price: number;
}

function calculateTotalSales(sales: Sale[]): number {
  if (!Array.isArray(sales)) {
    return 0;
  }
  return sales.reduce((total, sale) => total + (sale.quantity * sale.price), 0);
}

function getExpensiveProducts(sales: Sale[], minPrice: number): string[] {
  if (!Array.isArray(sales)) {
    return [];
  }
  return sales
    .filter(sale => sale.price * sale.quantity >= minPrice) // Filter by total sale value
    .map(sale => sale.product); // Map to just product names
}

function summarizeSalesByCategory(sales: Sale[]): { [category: string]: number } {
  if (!Array.isArray(sales)) {
    return {};
  }
  return sales.reduce((acc, sale) => {
    let category = 'Other';
    const productNameLower = sale.product.toLowerCase();
    if (productNameLower.includes('laptop') || productNameLower.includes('mouse')) {
      category = 'Electronics';
    } else if (productNameLower.includes('book') || productNameLower.includes('novel')) {
      category = 'Books';
    }

    const revenue = sale.quantity * sale.price;
    acc[category] = (acc[category] || 0) + revenue;
    return acc;
  }, {});
}

