  { product: 'Wireless Mouse', quantity: 3, price: 25 },
  { product: 'The Great Novel', quantity: 2, price: 20 },
  { product: 'USB Hub', quantity: 5, price: 10 },
  { product: 'Gaming Keyboard', quantity: 1, price: 80 },
  { product: 'Science Book', quantity: 1, price: 30 }
];

// Test calculateTotalSales
