tasks.solutionCode:
class CircularBuffer<T> {
  private buffer: T[];
  private capacity: number;
  private head: number; // Points to the first (oldest) element
  private tail: number; // Points to the next available slot for a new element
  private currentSize: number;

  constructor(capacity: number) {
    if (capacity <= 0) {
      throw new Error('Capacity must be a positive integer.');
    }
    this.capacity = capacity;
    this.buffer = new Array<T>(capacity);
    this.head = 0;
    this.tail = 0;
    this.currentSize = 0;
  }

  add(element: T): void {
    this.buffer[this.tail] = element; // Place new element at tail
    this.tail = (this.tail + 1) % this.capacity; // Move tail pointer

    if (this.isFull()) {
      // If buffer was full before adding, the head pointer also moves
      // because the new element overwrote the old head.
      this.head = this.tail; 
    } else {
      // If buffer was not full, increment size
      this.currentSize++;
    }
  }

  getElements(): T[] {
    if (this.isEmpty()) {
      return [];
    }

    const result: T[] = [];
    if (this.tail > this.head) {
      // No wrap-around yet: elements are head to tail-1
      for (let i = this.head; i < this.tail; i++) {
        result.push(this.buffer[i]);
      }
    } else if (this.currentSize > 0) {
      // Wrapped around: elements are from head to end, then from start to tail-1
      for (let i = this.head; i < this.capacity; i++) {
        result.push(this.buffer[i]);
      }
      for (let i = 0; i < this.tail; i++) {
        result.push(this.buffer[i]);
      }
    }
    return result;

    // Alternative for getElements using slice and concat (more functional, potentially less efficient for very large buffers):
    // if (this.isEmpty()) return [];
    // if (this.tail > this.head) {
    //   return this.buffer.slice(this.head, this.tail);
    // } else if (this.currentSize > 0) {
    //   return this.buffer.slice(this.head).concat(this.buffer.slice(0, this.tail));
    // } 
    // return [];
  }

  isFull(): boolean {
    return this.currentSize === this.capacity;
  }

  isEmpty(): boolean {
    return this.currentSize === 0;
  }
}

// Test Cases:
console.log('--- Test CircularBuffer of capacity 3 ---');
const buffer3 = new CircularBuffer<string>(3);
console.log('Initial empty:', buffer3.getElements(), 'Size:', buffer3.currentSize, 'Full:', buffer3.isFull(), 'Empty:', buffer3.isEmpty());

buffer3.add('A'); // [A, _, _]
console.log('Add A:', buffer3.getElements(), 'Size:', buffer3.currentSize);
buffer3.add('B'); // [A, B, _]
console.log('Add B:', buffer3.getElements(), 'Size:', buffer3.currentSize);
buffer3.add('C'); // [A, B, C] -> Full
console.log('Add C:', buffer3.getElements(), 'Size:', buffer3.currentSize, 'Full:', buffer3.isFull());

buffer3.add('D'); // [D, B, C] -> Overwrites A, head moves to B's position conceptually (tail is D's next slot)
console.log('Add D (overwrite A):', buffer3.getElements(), 'Size:', buffer3.currentSize);

buffer3.add('E'); // [D, E, C] -> Overwrites B
console.log('Add E (overwrite B):', buffer3.getElements(), 'Size:', buffer3.currentSize);

console.log('Is Full:', buffer3.isFull()); // true
console.log('Is Empty:', buffer3.isEmpty()); // false

console.log('\n--- Test CircularBuffer of capacity 1 ---');
const buffer1 = new CircularBuffer<number>(1);
console.log('Initial empty:', buffer1.getElements());
buffer1.add(10);
console.log('Add 10:', buffer1.getElements()); // [10]
buffer1.add(20);
console.log('Add 20 (overwrite 10):', buffer1.getElements()); // [20]

console.log('\n--- Test Edge Case: Capacity 0/Negative ---');
try {
  new CircularBuffer<any>(0);
} catch (e) {
  console.error(e.message); // Should throw error
}

