function removeFirstItem(items) {
  // Immutable alternative using slice()
  if (!Array.isArray(items) || items.length === 0) {
    return []; // Return an empty array for empty or invalid input
  }
  return items.slice(1);
}

function updateItemAtIndex(items, index, newItem) {
  // Immutable alternative using map() or slice() with spread syntax
  if (!Array.isArray(items) || index < 0 || index >= items.length) {
    return [...items]; // Return a shallow copy if index is out of bounds or invalid, or original if immutable return is desired for no change
  }

  // Option 1: Using map() for clarity for item-by-item transformation
  return items.map((item, idx) => (idx === index ? newItem : item));

  // Option 2: Using slice() and spread syntax for direct construction
  // return [...items.slice(0, index), newItem, ...items.slice(index + 1)];
}

