tasks.solutionCode:
function manageInventory(inventory, newProducts, startIndex, discontinueIndex, discontinueCount, fillCount) {
  // Ensure inventory is an array
  if (!Array.isArray(inventory)) {
    throw new Error('Inventory must be an array.');
  }

  // 1. Restock (using splice)
  if (newProducts && newProducts.length > 0) {
    inventory.splice(startIndex, 0, ...newProducts);
  }

  // 2. Discontinue (using splice)
  if (discontinueCount > 0) {
    inventory.splice(discontinueIndex, discontinueCount);
  }

  // 3. Sort Inventory (using sort)
  inventory.sort((a, b) => a - b);

  // 4. Fill Placeholders (using fill)
  if (fillCount > 0) {
    inventory.fill(null, 0, fillCount);
  }

  return inventory;
}

