tasks.solutionCode:
function analyzeSales(salesRecords) {
  if (!Array.isArray(salesRecords)) {
    throw new Error('Input must be an array of sales records.');
  }

  const highValueSales = salesRecords.filter(sale => sale.amount * sale.quantity >= 500);

  const transformedSales = highValueSales.map(sale => ({
    transactionId: sale.transactionId,
    itemName: sale.itemName,
    totalPrice: sale.amount * sale.quantity
  }));

  const totalRevenue = transformedSales.reduce((acc, sale) => acc + sale.totalPrice, 0);

  // To get unique items, map to item names and then use Set to filter duplicates
  const uniqueItemsSold = [...new Set(highValueSales.map(sale => sale.itemName))];

  return {
    totalRevenue,
    uniqueItemsSold
  };
}

