function processUsers(users) {
  if (!Array.isArray(users)) {
    throw new Error('Input must be an array of user objects.');
  }

  const hasAdmins = users.some(user => user.username === 'admin');

  const allActive = users.every(user => user.isActive);

  const firstInactiveUser = users.find(user => !user.isActive) || null;

  // For indexOfSpecificUser, we need to find an object matching two criteria.
  // findIndex is suitable here.
  const indexOfSpecificUser = users.findIndex(user => user.username === 'guest' && user.id === 99);

  // isGuestPresent can be done with some() as well, for consistency with hasAdmins
  const isGuestPresent = users.some(user => user.username === 'guest');

  return {
    hasAdmins,
    allActive,
    firstInactiveUser,
    indexOfSpecificUser,
    isGuestPresent
  };
}

