class DynamicList {
  constructor(initialItems = []) {
    this.items = [...initialItems];
  }

  addItem(item) {
    this.items.push(item);
  }

  removeItem(item) {
    const index = this.items.indexOf(item);
    if (index > -1) {
      this.items.splice(index, 1);
      return true;
    }
    return false;
  }

  moveItem(item, newIndex) {
    const oldIndex = this.items.indexOf(item);
    if (oldIndex === -1) {
      return false; // Item not found
    }

    let normalizedNewIndex = newIndex;
    if (newIndex < 0) {
      normalizedNewIndex = this.items.length + newIndex; // Calculate index from end
    }

    // Clamp newIndex to valid bounds: [0, this.items.length - 1]
    normalizedNewIndex = Math.max(0, Math.min(normalizedNewIndex, this.items.length - 1));

    const [movedItem] = this.items.splice(oldIndex, 1); // Remove item
    this.items.splice(normalizedNewIndex, 0, movedItem); // Insert at new position
    return true;
  }

  getList() {
    return [...this.items]; // Return a copy
  }

  shuffle() {
    for (let i = this.items.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [this.items[i], this.items[j]] = [this.items[j], this.items[i]]; // Swap elements
    }
  }
}

